type JWSDecodedHeader struct {
	Alg string   `json:"alg"`
	Kid string   `json:"kid"`
	X5c []string `json:"x5c"`
}

type IosV2SignedPayload struct {
	NotificationType string    `json:"notificationType"`
	NotificationUUID string    `json:"notificationUUID"`
	Data             IosV2Data `json:"data"`
	Version          string    `json:"version"`
}

type IosV2Data struct {
	AppAppleId            int64  `json:"appAppleId"`
	BundleId              string `json:"bundleId"`
	BundleVersion         string `json:"bundleVersion"`
	Environment           string `json:"environment"` // v2是Production，不是PROD哦
	SignedTransactionInfo string `json:"signedTransactionInfo"`
}

type JWSTransactionDecodedPayload struct {
	TransactionId         string `json:"transactionId"`
	OriginalTransactionId string `json:"originalTransactionId"`
	BundleId              string `json:"bundleId"`
	ProductId             string `json:"productId"`
	PurchaseDate          int64  `json:"purchaseDate"`
	OriginalPurchaseDate  int64  `json:"originalPurchaseDate"`
	Quantity              int64  `json:"quantity"`
	Type                  string `json:"type"`
	InAppOwnershipType    string `json:"inAppOwnershipType"`
	SignedDate            int64  `json:"signedDate"`
	RevocationReason      int64  `json:"revocationReason"`
	RevocationDate        int64  `json:"revocationDate"`
}

type MyJWSClaim struct {
	IosV2SignedPayload
	jwt.StandardClaims
}

