//本实例将苹果退款回调通知的数据进行验签。
//数据是JWS标准。
//步骤：
//1、取出jws字符串中的header，取出header中的x5c，x5c是包含多个证书的数组
//2、用该数组的证书和苹果的系统级证书放到一个array里面
//3、循环该数组，调用cert.CheckSignatureFrom()方法
//4、通过后，再验签x5c中第一个数组元素和数据 是否匹配
func main() {
	//初始化证书
	if err := initCertificate(); err != nil {
		log.Fatalln(fmt.Sprintf("init certificate failed error:%v", err))
	}
	//处理jws串
	//jwsString := "..PLYzUWpjhEihWuMiBgYpU6PoVCTuWtn9KMvNn-nAVSnt2MNwvA7lvisIjkRkRSA0MtOC_WO-pddjeFvp_aPxnA"
	jwsString := "..R-EZzbev00Ij-4QE5itPML70ixBcuRkXu51iPmMi3Ut9BBQ6IknWFGJeoBrfcA7LbEF9BbscDT6PyQCXl2gpiQ"
	err := handleJWS(jwsString)
	if err != nil {
		log.Fatalln(err)
	}
	log.Println("success")
}

func initCertificate() error {
	caFile, err := ioutil.ReadFile(APPLE_ROOT_CERTIFICATE_PATH)
	if err != nil {
		return err
	}

	APPLE_ROOT_CERTIFICATE, err = x509.ParseCertificate(caFile)
	if err != nil {
		return errors.New(fmt.Sprintf("first ParseCertificate error,err:%v", err))
	}

	return nil
}

func handleJWS(jwsString string) error {
	jwsArray := strings.Split(jwsString, ".")
	if len(jwsArray) != 3 {
		return errors.New("len of jws array != 3")
	}

	//JWS的header包含算法和几个证书
	var jwsHeader JWSDecodedHeader
	jwsHeaderBytes, err := base64.RawStdEncoding.DecodeString(jwsArray[0])
	if err != nil {
		return errors.New(fmt.Sprintf("decode jws header error, err:%v", err))
	}

	if err := json.Unmarshal(jwsHeaderBytes, &jwsHeader); err != nil {
		return errors.New(fmt.Sprintf("unmarshal jws header error, err:%v", err))
	}

	//JWS的payload包含明文，可以直接读取，但最好验签一下，然后从token里面读取。
	//var jwsPayload JWSTransactionDecodedPayload
	//jwsPayloadBytes, err := base64.RawStdEncoding.DecodeString(jwsArray[1])
	//if err != nil {
	//	return errors.New(fmt.Sprintf("decode jws payload error, err:%v", err))
	//}
	//
	//if err := json.Unmarshal(jwsPayloadBytes, &jwsPayload); err != nil {
	//	return errors.New(fmt.Sprintf("unmarshal jws payload error, err:%v", err))
	//}

	//签名
	//jwsSignBytes, err := base64.RawStdEncoding.DecodeString(jwsArray[2])
	//if err != nil {
	//	return errors.New(fmt.Sprintf("decode jws sign error, err:%v", err))
	//}
	//fmt.Println(string(jwsSignBytes))

	if len(jwsHeader.X5c) <= 0 {
		return errors.New(fmt.Sprintf("len of jwsHeader.X5c <= 0"))
	}

	// 把x5c中的证书字符串转换为pem标准格式的证书
	pemChainsFormatted := convertPemFormatted(jwsHeader.X5c)

	// 把pem格式转为证书
	var certificateChains []*x509.Certificate
	for i, pemItem := range pemChainsFormatted {
		cer, err := loadCertificateFromPemString(pemItem)
		if err != nil {
			return errors.New(fmt.Sprintf("load certificate from pem string error, num=%d, err:%v", i, err))
		}
		certificateChains = append(certificateChains, cer)
	}

	// 把根证书append到证书链
	certificateChains = append(certificateChains, APPLE_ROOT_CERTIFICATE)

	certificateChains[1], certificateChains[2] = certificateChains[2], certificateChains[1]
	//验证证书链
	if err := verifyCertChains(certificateChains); err != nil {
		return err
	}

	//验签数据
	if len(certificateChains) <= 0 {
		return errors.New(fmt.Sprintf("len of pemChainsFormatted <= 0"))
	}

	// 第一个证书
	firstCert := certificateChains[0]

	//验签
	var respClaim MyJWSClaim
	_, err = jwt.ParseWithClaims(jwsString, &respClaim, func(token *jwt.Token) (interface{}, error) {
		return firstCert.PublicKey, nil
	})

	if err != nil {
		return err
	}

	var resp IosV2SignedPayload
	resp = respClaim.IosV2SignedPayload

	fmt.Println(resp)

	return nil
}

func verifyCertChains(certificateChains []*x509.Certificate) error {
	if len(certificateChains) <= 1 {
		return errors.New("verifyCertChains failed, len(verifyCertChains)<=1")
	}

	// 循环证书链，证书链中第一个证书是由第二个证书签发的，第二个是由第三个签发的，依次。。。
	// 如果有两个需要验证一次，如果有三个需要验证两次，依次。。。
	for i, _ := range certificateChains {
		//最后一个证书为跟证书，无需验证
		if i+1 == len(certificateChains) {
			break
		}

		//加载子证书
		childCert := certificateChains[i]

		//加载父证书
		parentCert := certificateChains[i+1]

		//验证 子证书 是否由 父证书 签发的
		if err := childCert.CheckSignatureFrom(parentCert); err != nil {
			return errors.New(fmt.Sprintf("check signaturefrom error; num=%d, err:%v", i, err))
		}

		fmt.Printf("CheckSignatureFrom success, num=%d \r\n", i)
	}

	return nil
}

func convertPemFormatted(pemChains []string) []string {
	var pemChainsFormatted []string
	for _, pemString := range pemChains {
		var pemRune []rune
		for i, pemSingleRune := range []rune(pemString) {
			pemRune = append(pemRune, pemSingleRune)
			if (i+1)%64 == 0 {
				pemRune = append(pemRune, '\n')
			}
		}
		temp := "-----BEGIN CERTIFICATE-----\n" + string(pemRune) + "\n-----END CERTIFICATE-----"
		pemChainsFormatted = append(pemChainsFormatted, temp)
	}

	return pemChainsFormatted
}

func loadCertificateFromPemString(pemString string) (*x509.Certificate, error) {
	blocker, _ := pem.Decode([]byte(pemString))
	if blocker == nil {
		return nil, errors.New("first pem.Decode error, blocker is nil")
	}

	cert, err := x509.ParseCertificate(blocker.Bytes)
	if err != nil {
		return nil, errors.New(fmt.Sprintf("first ParseCertificate error,err:%v", err))
	}

	return cert, nil
}
